mapper.update:
UPDATE community
SET viewCnt = viewCnt+1
WHERE communityId = #{communityId}

UPDATE community
SET category = #{category}, title = #{title}, content = #{content}, regDate = now()
WHERE communityId = #{communityId}

mapper.select:
SELECT communityId, userId, category, title, content, date_format(regDate, '%Y-%M-%d') as regDate, viewCnt FROM community
WHERE ${key} LIKE concat('%', #{word}, '%')
ORDER BY ${orderBy} ${orderByDir}
