mapper.select:
SELECT review_id as reviewId, user_id as userId, video_id as videoId, title, content, date_format(reg_date, '%Y-%M-%d') as regDate, view_cnt as viewCnt
FROM review
WHERE video_id=#{videoId}

SELECT review_id as reviewId, user_id as userId, video_id as videoId, title, content, date_format(reg_date, '%Y-%M-%d') as regDate, view_cnt as viewCnt
FROM review
WHERE review_id=#{reviewId}

mapper.update:
UPDATE review
SET title=#{title}, content=#{content}, reg_date = now()
WHERE review_id=#{reviewId}

UPDATE review
SET view_cnt = view_cnt+1
WHERE review_id=#{reviewId}
