mapper.select:
		SELECT video_id as videoId, title, url, fitpart, youtuber, view_cnt as viewCnt, date_format(reg_date, '%Y-%M-%d') as regDate
		FROM video



         		${key} LIKE concat('%', #{word}, '%') AND fitpart = #{fitpart}


         		${key} LIKE concat('%', #{word}, '%')


        		fitpart = #{fitpart}




			ORDER BY ${orderBy} ${orderByDir}

SELECT video_id as videoId, title, url, fitpart, youtuber, view_cnt as viewCnt, date_format(reg_date, '%Y-%M-%d') as regDate
FROM video
WHERE video_id = #{videoId};

mapper.update:
UPDATE video
SET view_cnt = view_cnt+1
WHERE video_id = #{videoId}
